import React, { useState } from 'react';

const Home = () => {
  const token = localStorage.getItem('token');
  console.log(token);
  // Estado para almacenar la lista de usuarios y el usuario nuevo
  const [users, setUsers] = useState([
    { id: 1, name: 'Juan', age: 25 },
    { id: 2, name: 'María', age: 30 },
    { id: 3, name: 'Pedro', age: 28 },
  ]);
  const [newUser, setNewUser] = useState({ name: '', age: '' });

  // Función para manejar cambios en el formulario
  const handleInputChange = event => {
    const { name, value } = event.target;
    setNewUser({ ...newUser, [name]: value });
  };

  // Función para agregar un nuevo usuario
  const addUser = event => {
    event.preventDefault();
    if (newUser.name.trim() === '' || newUser.age.trim() === '') {
      alert('Por favor ingresa el nombre y la edad del usuario.');
      return;
    }
    const newUserObj = {
      id: users.length + 1,
      name: newUser.name,
      age: parseInt(newUser.age),
    };
    setUsers([...users, newUserObj]);
    setNewUser({ name: '', age: '' });
  };

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      <form onSubmit={addUser}>
        <label htmlFor="name">Nombre:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={newUser.name}
          onChange={handleInputChange}
        />
        <label htmlFor="age">Edad:</label>
        <input
          type="text"
          id="age"
          name="age"
          value={newUser.age}
          onChange={handleInputChange}
        />
        <button type="submit">Agregar Usuario</button>
      </form>
      <div className="user-list">
        <h2>Lista de Usuarios</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Edad</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.age} años</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="stats">
        <h2>Estadísticas</h2>
        <p>Total de usuarios: {users.length}</p>
        <p>
          Edad promedio:{' '}
          {users.reduce((acc, user) => acc + user.age, 0) / users.length}
        </p>
      </div>
    </div>
  );
};

export default Home;
