import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./styles.css"
function Reports() {
  const token = localStorage.getItem('token');
  console.log(token);


  const [reports, setReports] = useState([]);

  const fetchReports = async () => {
    try {
      const response = await axios.get(
        'https://ujed-api.onrender.com/api/reports?limit=10&offset=0&order=asc',
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setReports(response.data);
    } catch (error) {
      console.error('Error al obtener reportes:', error);
    }
  };

  // Llamamos a fetchUsers y fetchReports al cargar el componente
  useEffect(() => {
    fetchReports();
  }, []); // La lista de depend

  return (
    <div>
       <h1 className='main-title'>Dashboard</h1>
       <div className="report-list">
      <h2 className='title' >Lista de Reportes</h2>
     <div className="table-container">
     <table>
        <thead className='title-t'>
          <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Descripción</th>
            <th>Estado</th>
            <th>Ubicación</th>
            <th>Usuario</th>
            <th>Imágenes</th>
            <th>Creado</th>
            <th>Actualizado</th>
          </tr>
        </thead>
        <tbody className='body'>
          {reports.map(report => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.title}</td>
              <td>{report.description}</td>
              <td>{report.status}</td>
              <td>{report.location}</td>
              <td>{report.user.name}</td>
              <td>
                  {/* Mostrar solo la primera imagen del reporte */}
                  {report.images.length > 0 && (
                    <img src={report.images[0].url} alt="Reporte" style={{ maxWidth: '100px', maxHeight: '100px' }} />
                  )}
                </td>
              <td>{new Date(report.created_at).toLocaleString()}</td>
              <td>{new Date(report.updated_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
     </div>
    </div>
    </div>

  )
}

export default Reports
