import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faRightFromBracket, faHouse, faUser } from '@fortawesome/free-solid-svg-icons';

import "./styles.css"

const Home = () => {
  const token = localStorage.getItem('token');
  console.log(token);

  // Estado para almacenar la lista de usuarios
  const [users, setUsers] = useState([]);
  const [reports, setReports] = useState([]);
  const [showSidebar, setShowSidebar] = useState(true);

  // FunciÃ³n para obtener los usuarios desde el API
  const fetchUsers = async () => {
    try {
      const response = await axios.get(
        'https://ujed-api.onrender.com/api/users?order_by=created_at&order=desc&limit=5',
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setUsers(response.data.slice(0, 4));
    } catch (error) {
      console.error('Error al obtener usuarios:', error);
    }
  };

  const fetchReports = async () => {
    try {
      const response = await axios.get(
        'https://ujed-api.onrender.com/api/reports?limit=10&offset=0&order=asc',
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setReports(response.data.slice(0, 3));
    } catch (error) {
      console.error('Error al obtener reportes:', error);
    }
  };

  // Llamamos a fetchUsers y fetchReports al cargar el componente
  useEffect(() => {
    fetchUsers();
    fetchReports();
  }, []); // La lista de depend

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  // Llamamos a fetchUsers al cargar el componente

  return (
    <div className={`dashboard-container ${showSidebar ? '' : 'hide-sidebar'}`}>
      <div className="sidebar">

      <FontAwesomeIcon icon={faBars} className="burger-icon" onClick={toggleSidebar}  />


        <nav>
          <ul>
            <li>
              <Link to={'/Home'}><FontAwesomeIcon icon={faHouse} /> Dashboard</Link>
            </li>
            <li>
              <Link to={'/Users'}><FontAwesomeIcon icon={faUser} /> Usuarios</Link>
            </li>
            <li>
              <Link to={'/Reports'}>Reportes</Link>
            </li>
            <li className='cerrar'>
              <Link to={'/'}><FontAwesomeIcon icon={faRightFromBracket} /></Link>
            </li>
          </ul>
        </nav>
      </div>
    <h1 className='main-title'>Dashboard</h1>
    <div className="user-list">
      <h2 className='title'>Lista de Usuarios</h2>
      <div className="table-container">
      <table>
          <thead className='title-t'>
            <tr className='header'>
              <th>ID</th>
              <th>Nombre</th>
              <th>Apellido</th>
              <th>Email</th>
              <th>Roles</th>
              <th>Creado</th>
              <th>Actualizado</th>
            </tr>
          </thead>
          <tbody className='body'>
            {users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.last_name}</td>
                <td>{user.email}</td>
                <td>{user.roles.join(', ')}</td>
                <td>{new Date(user.created_at).toLocaleString()}</td>
                <td>{new Date(user.updated_at).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
    <div className="report-list">
      <h2 className='title' >Lista de Reportes</h2>
     <div className="table-container">
     <table>
        <thead className='title-t'>
          <tr>
            <th>ID</th>
            <th>TÃ­tulo</th>
            <th>DescripciÃ³n</th>
            <th>Estado</th>
            <th>UbicaciÃ³n</th>
            <th>Usuario</th>
            <th>ImÃ¡genes</th>
            <th>Creado</th>
            <th>Actualizado</th>
          </tr>
        </thead>
        <tbody className='body'>
          {reports.map(report => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.title}</td>
              <td>{report.description}</td>
              <td>{report.status}</td>
              <td>{report.location}</td>
              <td>{report.user.name}</td>
              <td>
                  {/* Mostrar solo la primera imagen del reporte */}
                  {report.images.length > 0 && (
                    <img src={report.images[0].url} alt="Reporte" style={{ maxWidth: '100px', maxHeight: '100px' }} />
                  )}
                </td>
              <td>{new Date(report.created_at).toLocaleString()}</td>
              <td>{new Date(report.updated_at).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
     </div>
    </div>
  </div>
);
};

export default Home;
